INSERT INTO public.item(availability, description, price, title, manufacturer_id, subcategory_id)	VALUES ('ABSENT',	'из имевшихся в продаже чайников до 1000 грн. выбрал именно этот. Первое на что обратил внимание - без запаха, у остальных из пластика даже в этой ценовой категории запах пластика был от явного вонючего до чуть заметного. В достоинствах и недостатках укажу первое впечатление, а время покажет насколько будет долговечен. На упаковке производитель заявляет пробег чайника - 7500 кипячений. По обслуживанию при выборе чайника и покупке его замечаний нет. Спасибо за оперативные ответы по существу без воды" в Комфи в МОСТ-сити (Днепр).',	999, 'HD9305/21',	7,	5);
